(()=>{document.addEventListener("DOMContentLoaded",function(){let e=document.getElementById("comments-link");if(!e)return;let t=window.location.pathname.replace(/^\//,"").replace(/\.html$/,"");fetch("/assets/data/comments.json?ts="+Date.now()).then(n=>n.json()).then(n=>{if(n[t]){let o=n[t];e.innerHTML=`\u{1F4AC} ${o} \u6761\u8BC4\u8BBA`}else e.innerHTML="\u{1F4AC} \u6682\u65E0\u8BC4\u8BBA"}).catch(n=>{console.error("\u83B7\u53D6\u8BC4\u8BBA\u6570\u636E\u5931\u8D25:",n),e.innerHTML="\u{1F4AC} \u8BC4\u8BBA\u52A0\u8F7D\u5931\u8D25"})});})();
